mapper.select:
SELECT 

FROM ts_server a

WHERE a.id = #{id}

SELECT a.id, a.parent_id AS "parent.id", a.parent_ids FROM ts_server a
a.del_flag = #{DEL_FLAG_NORMAL} AND a.parent_ids LIKE #{parentIds}
ORDER BY a.sort ASC

mapper.update:
UPDATE ts_server SET 
	parent_id = #{parent.id}, 
	parent_ids = #{parentIds}
WHERE id = #{id}
