mapper.select:
SELECT 

FROM ts_config a

WHERE a.id = #{id}

SELECT
FROM ts_config a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.label LIKE '%'||#{label}||'%' '%'+#{label}+'%' concat('%',#{label},'%')
AND a.key LIKE '%'||#{key}||'%' '%'+#{key}+'%' concat('%',#{key},'%')
AND a.type = #{type}
AND a.sync_method = #{syncMethod}
AND a.sync_system = #{syncSystem}
AND a.remarks LIKE '%'||#{remarks}||'%' '%'+#{remarks}+'%' concat('%',#{remarks},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM ts_config a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE ts_config SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
